import React, { Component } from "react";
import { db } from "../fire";
import { Container, Table } from "reactstrap";
import QRModal from "./QRModal";
const styles = {
  marginTop: "80px"
};

export default class ModelsList extends Component {
  constructor(props) {
    super(props);
    this.state = { model_data: [] }; // <- set up react state
  }
  fetchFromDB = () => {
    db.collection("Models")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        // console.log(data);
        this.setState({ model_data: data }); // array of cities objects
      });
  };
  componentWillMount() {
    this.fetchFromDB();
  }
  render() {
    return (
      <Container>
        <Table striped style={styles}>
          <thead>
            <tr>
              <th>#</th>
              <th>Model name</th>
              <th>Diseases involved</th>
              <th>Description</th>
              <th>QR-code</th>
            </tr>
          </thead>
          <tbody>
            {/* Render the list of messages */
            this.state.model_data.map((data, index) => (
              <tr key={index}>
                <th scope="row">{index}</th>
                <td>{data.name}</td>
                <td>
                  <ul>
                    {data.labels.map((label, key) => (
                      <li key={key}>{label}</li>
                    ))}
                  </ul>
                </td>
                <td>{data.desciption}</td>
                <td>
                  <QRModal name={data.name} />
                </td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    );
  }
}
