import React, { Component } from "react";
import { Form, FormGroup, Input, Label, Alert, Button } from "reactstrap";
import axios from "axios";

const styles = {
  marginTop: "80px"
};
const stylestwo = {
  textAlign: "center",

  mariginTop: "10px",
  padding: "20px"
};
class ClusterForm extends Component {
  state = {
    Folder_Name: "",
    fileType: "Images",
    error_data: ""
  };
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = event => {
    event.preventDefault();
    const { Folder_Name } = this.state;

    const url = "http://0.0.0.0:5001/image-cluster/" + Folder_Name;
    axios
      .get(url, {
        responseType: "arraybuffer"
      })
      .then(response =>
        console.log(Buffer.from(response.data, "binary").toString("base64"))
      );
    console.log(this.state);
  };

  render() {
    return (
      <div>
        <Form onSubmit={this.onSubmit} style={styles}>
          <h5>
            <strong>Configure Newly uploaded folder:</strong>
          </h5>
          <FormGroup>
            <Label for="Folder_Name">Folder-Name:</Label>
            <Input
              type="text"
              name="Folder_Name"
              id="Folder_Name"
              value={this.state.Folder_Name}
              onChange={this.onChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="fileType">File-type:</Label>
            <Input
              type="select"
              name="fileType"
              id="fileType"
              onChange={this.onChange}
              value={this.state.fileType}
            >
              <option>Images</option>
              <option disabled>Tabular</option>
            </Input>
          </FormGroup>

          <br />
          <Button color="primary">Clusterify</Button>

          <br />
          {this.state.error_data && (
            <div style={stylestwo}>
              <p>
                <Alert color="danger">{this.state.error_data}</Alert>
              </p>
            </div>
          )}
          {this.state.success_data && (
            <div style={stylestwo}>
              <p>
                <Alert color="success">{this.state.success_data}</Alert>
              </p>
            </div>
          )}
        </Form>
      </div>
    );
  }
}

export default ClusterForm;
