import React, { Component } from "react";
import axios from "axios";
import { Button, Input, Alert } from "reactstrap";

const styles = {
  marginTop: "80px"
};
const stylestwo = {
  textAlign: "center",

  mariginTop: "10px",
  padding: "20px"
};

export default class Inference extends Component {
  state = { selectedFile: null, prediction: "", error: "" };

  fileChangedHandler = event => {
    this.setState({ selectedFile: event.target.files[0] });
  };
  uploadHandler = () => {
    const formData = new FormData();
    formData.append(
      "file",
      this.state.selectedFile,
      this.state.selectedFile.name
    );
    axios
      .post(
        "https://api-2445582824322.staging.gw.apicast.io/deploy/Skin-Cancer-Detector/a/?user_key=402d46c6d21b86e66339d12e79e05366",
        formData
      )
      .then(res => {
        console.log(res.data);
        if (res.data.prediction) {
          this.setState({ prediction: res.data.prediction });
        } else {
          this.setState({ error: "Inference did not work! Check again later" });
        }
      });
  };

  render() {
    return (
      <div style={styles}>
        <Input type="file" onChange={this.fileChangedHandler} />
        <br />
        <Button onClick={this.uploadHandler}>Upload!</Button>

        {this.state.error && (
          <div style={stylestwo}>
            <p>
              <Alert color="danger">{this.state.error_data}</Alert>
            </p>
          </div>
        )}
        {this.state.prediction && (
          <div style={stylestwo}>
            <Alert color="success">
              An email will be sent to you after the training has completed.
            </Alert>
          </div>
        )}
      </div>
    );
  }
}
