import React, { Component } from "react";
import { db } from "../fire";
import { Container, Table } from "reactstrap";

export default class ModelsList extends Component {
  constructor(props) {
    super(props);
    this.state = { model_data: [] }; // <- set up react state
  }
  componentWillMount() {
    /* Create reference to messages in Firebase Database */
    db.collection("Models")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        console.log(data);
        this.setState({ model_data: data }); // array of cities objects
      });
  }
  render() {
    return (
      <Container>
        <Table striped>
          <thead>
            <tr>
              <th>#</th>
              <th>Model name</th>
              <th>Diseases involved</th>
              <th>Description</th>
            </tr>
          </thead>
          <tbody>
            {/* Render the list of messages */
            this.state.model_data.map((data, index) => (
              <tr key={index}>
                <th scope="row">{index}</th>
                <td>{data.name}</td>
                <td>{data.names}</td>
                <td>{data.desciption}</td>
              </tr>
            ))}
          </tbody>
        </Table>
      </Container>
    );
  }
}
