import React, { Component } from "react";
import { Button, Form, FormGroup, Label, Input, ButtonGroup } from "reactstrap";
import { db } from "../fire";
class DeployForm extends Component {
  state = {
    model_name: "",
    description: "",
    diseases: [],
    labels: []
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onCheckboxBtnClick = selected => {
    var array = [...this.state.diseases]; // make a separate copy of the array
    var index = array.indexOf(selected);
    if (index < 0) {
      array.push(selected);
    } else {
      array.splice(index, 1);
    }

    this.setState({ diseases: array });
  };

  onSubmit = async event => {
    event.preventDefault();
    //const { first, last, email, mobile, purpose } = this.state;
    console.log(this.state);

    // try {
    //   res = await axios.post("https://easyq-backend.herokuapp.com/makeTicket", {
    //     first: first,
    //     last: last,
    //     email: email,
    //     mobile: mobile,
    //     purpose: purpose
    //   });
    // } catch (error) {
    //   console.log(error.message);
    // }
  };

  componentDidMount() {
    db.collection("Images")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        //console.log(data);
        var temp = [];
        for (var i = 0; i < data.length; i++) {
          temp.push(data[i].label);
        }
        //console.log(temp);
        this.setState({ labels: temp }); // array of cities objects
      });
  }

  render() {
    return (
      <Form onSubmit={this.onSubmit}>
        <FormGroup>
          <Label for="Model-Name">Model Name:</Label>
          <Input
            type="text"
            name="model_name"
            id="model_name"
            value={this.state.model_name}
            onChange={this.onChange}
            required
          />
        </FormGroup>
        <FormGroup>
          <Label for="description">Description:</Label>
          <Input
            type="text"
            name="description"
            id="description"
            value={this.state.description}
            onChange={this.onChange}
            required
          />
        </FormGroup>
        <p>Select Diseases:</p>
        <ButtonGroup>
          {this.state.labels.map((label, index) => (
            <Button
              color="primary"
              onClick={() => this.onCheckboxBtnClick(label)}
              active={this.state.diseases.includes(label)}
              key={index}
            >
              {label}
            </Button>
          ))}
        </ButtonGroup>
        <br />
        <Button>Submit</Button>
      </Form>
    );
  }
}

export default DeployForm;
