import React, { Component } from "react";
import axios from "axios";
import { Button, Input, Alert, Label } from "reactstrap";
import { db } from "../fire";

const styles = {
  marginTop: "80px"
};
const stylestwo = {
  textAlign: "center",

  mariginTop: "10px",
  padding: "20px"
};

export default class Inference extends Component {
  state = {
    selectedFile: null,
    prediction: "",
    error: "",
    model_data: [],
    model: "--select--"
  };

  componentDidMount() {
    db.collection("Models")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        this.setState({ model_data: data });
      });
  }
  onChangeHandler = event => {
    this.setState({ model: event.target.value });
    console.log(event.target.value);
  };
  fileChangedHandler = event => {
    this.setState({ selectedFile: event.target.files[0] });
  };
  uploadHandler = () => {
    const formData = new FormData();
    formData.append(
      "file",
      this.state.selectedFile,
      this.state.selectedFile.name
    );
    axios
      .post(
        "https://api-2445582824322.staging.gw.apicast.io/deploy/Skin-Cancer-Detector/a/?user_key=402d46c6d21b86e66339d12e79e05366",
        formData
      )
      .then(res => {
        console.log(res.data);
        if (res.data.prediction) {
          this.setState({ prediction: res.data.prediction });
        } else {
          this.setState({ error: "Inference did not work! Check again later" });
        }
      });
  };

  render() {
    return (
      <div style={styles}>
        <Label for="selectModels">Select Model:</Label>
        <Input
          type="select"
          name="select"
          id="selectModels"
          value={this.state.model}
          onChange={this.onChangeHandler}
        >
          <option>--select--</option>
          {this.state.model_data.map((data, index) => (
            <option key={index}>{data.name}</option>
          ))}
        </Input>
        <br />
        <Input type="file" onChange={this.fileChangedHandler} />
        <br />
        <Button color="primary" onClick={this.uploadHandler}>
          Upload!
        </Button>

        {this.state.error && (
          <div style={stylestwo}>
            <p>
              <Alert color="danger">{this.state.error_data}</Alert>
            </p>
          </div>
        )}
        {this.state.prediction && (
          <div style={stylestwo}>
            <Alert color="success">
              The disease is {this.state.prediction}.
            </Alert>
          </div>
        )}
      </div>
    );
  }
}
