import React, { Component } from "react";
import { Form, FormGroup, Input, Label, Col, Alert, Button } from "reactstrap";

const styles = {
  marginTop: "80px"
};
const stylestwo = {
  textAlign: "center",

  mariginTop: "10px",
  padding: "20px"
};
class ConfigForm extends Component {
  state = {
    Folder_Name: ""
  };
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    return (
      <div>
        <Form onSubmit={this.onSubmit} style={styles}>
          <h5>
            <strong>Configure Newly uploaded folder:</strong>
          </h5>
          <FormGroup>
            <Label for="Folder_Name">Folder-Name:</Label>
            <Input
              type="text"
              name="Folder_Name"
              id="Folder_Name"
              value={this.state.Folder_Name}
              onChange={this.onChange}
              required
            />
          </FormGroup>
          <FormGroup tag="fieldset" Column>
            <legend className="col-form-label col-sm-2">File Type:</legend>
            <Col sm={10}>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="radio2" /> Image files
                </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="radio2" /> DICOM files
                </Label>
              </FormGroup>
            </Col>
          </FormGroup>
          <br />
          <Button color="primary">configure</Button>

          <br />
          {this.state.error_data && (
            <div style={stylestwo}>
              <p>
                <Alert color="danger">{this.state.error_data}</Alert>
              </p>
            </div>
          )}
        </Form>
      </div>
    );
  }
}

export default ConfigForm;
