import React, { Component } from "react";
import { db } from "../fire";
import { Table, Container, Row, Col } from "reactstrap";
import BarGraph from "./BarGraph";

class MyFileSystem extends Component {
  constructor(props) {
    super(props);
    this.state = { data: [] }; // <- set up react state
  }
  componentWillMount() {
    /* Create reference to messages in Firebase Database */
    db.collection("Images")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        console.log(data);
        this.setState({ data: data }); // array of cities objects
      });
  }
  render() {
    return (
      <Container>
        <Row>
          <Col xs="6">
            <Table striped>
              <thead>
                <tr>
                  <th>#</th>
                  <th>Disease name</th>
                  <th>Number of images</th>
                </tr>
              </thead>
              <tbody>
                {/* Render the list of messages */
                this.state.data.map((folder, index) => (
                  <tr key={index}>
                    <th scope="row">{index}</th>
                    <td>{folder.label}</td>
                    <td>{folder.names.length}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          </Col>
          <Col xs="6">
            <BarGraph data={this.state.data} />
          </Col>
        </Row>
      </Container>
    );
  }
}

export default MyFileSystem;
