import React, { Component } from "react";
import {
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  ButtonGroup,
  ButtonToolbar
} from "reactstrap";
import { db } from "../fire";
//import Loading from "./Loading";
const styles = {
  marginTop: "80px"
};

class DeployForm extends Component {
  state = {
    model_name: "",
    description: "",
    diseases: [],
    labels: []
  };

  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  onCheckboxBtnClick = selected => {
    var array = [...this.state.diseases]; // make a separate copy of the array
    var index = array.indexOf(selected);
    if (index < 0) {
      array.push(selected);
    } else {
      array.splice(index, 1);
    }

    this.setState({ diseases: array });
  };

  onSubmit = async event => {
    event.preventDefault();
    //const { description, model_name, diseases } = this.state;
    console.log(this.state);

    // try {
    //   res = await axios.post("https://easyq-backend.herokuapp.com/makeTicket", {
    //     first: first,
    //     last: last,
    //     email: email,
    //     mobile: mobile,
    //     purpose: purpose
    //   });
    // } catch (error) {
    //   console.log(error.message);
    // }
    this.setState({
      model_name: "",
      description: "",
      diseases: []
    });
  };

  fetchFromDB = () => {
    db.collection("Images")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => doc.data());
        //console.log(data);
        var temp = [];
        for (var i = 0; i < data.length; i++) {
          temp.push(data[i].label);
        }
        //console.log(temp);
        this.setState({ labels: temp }); // array of cities objects
      });
  };
  componentDidMount() {
    this.fetchFromDB();
  }

  render() {
    return (
      <div>
        <Form onSubmit={this.onSubmit} style={styles}>
          <FormGroup>
            <Label for="Model-Name">Model Name:</Label>
            <Input
              type="text"
              name="model_name"
              id="model_name"
              value={this.state.model_name}
              onChange={this.onChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="description">Description:</Label>
            <Input
              type="text"
              name="description"
              id="description"
              value={this.state.description}
              onChange={this.onChange}
              required
            />
          </FormGroup>
          <p>Select Diseases:</p>
          <ButtonToolbar>
            <ButtonGroup>
              {this.state.labels.map((label, index) => (
                <Button
                  color="success"
                  onClick={() => this.onCheckboxBtnClick(label)}
                  active={this.state.diseases.includes(label)}
                  key={index}
                >
                  {label}
                </Button>
              ))}
            </ButtonGroup>
            <ButtonGroup>
              <Button onClick={this.fetchFromDB}>
                <i class="fa fa-reload"></i>
              </Button>
            </ButtonGroup>
          </ButtonToolbar>
          <br />
          <br />
          <Button color="primary">Deploy</Button>
        </Form>
      </div>
    );
  }
}

export default DeployForm;
