import React, { Component } from "react";
import { Form, FormGroup, Input, Label, Alert, Button } from "reactstrap";
import { db } from "../fire";

const styles = {
  marginTop: "80px"
};
const stylestwo = {
  textAlign: "center",

  mariginTop: "10px",
  padding: "20px"
};
class ConfigForm extends Component {
  state = {
    Folder_Name: "",
    fileType: ""
  };
  onChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };
  onSubmit = event => {
    event.preventDefault();

    console.log(this.state);
  };

  render() {
    return (
      <div>
        <Form onSubmit={this.onSubmit} style={styles}>
          <h5>
            <strong>Configure Newly uploaded folder:</strong>
          </h5>
          <FormGroup>
            <Label for="Folder_Name">Folder-Name:</Label>
            <Input
              type="text"
              name="Folder_Name"
              id="Folder_Name"
              value={this.state.Folder_Name}
              onChange={this.onChange}
              required
            />
          </FormGroup>
          <FormGroup>
            <Label for="fileType">File-type:</Label>
            <Input
              type="select"
              name="fileType"
              id="fileType"
              onChange={this.onChange}
              value={this.state.fileType}
            >
              <option>Image</option>
              <option>Dicom</option>
            </Input>
          </FormGroup>

          <br />
          <Button color="primary">configure</Button>

          <br />
          {this.state.error_data && (
            <div style={stylestwo}>
              <p>
                <Alert color="danger">{this.state.error_data}</Alert>
              </p>
            </div>
          )}
        </Form>
      </div>
    );
  }
}

export default ConfigForm;
